import React from 'react';
import {PhotoSwipe} from 'react-photoswipe';
import {PhotoSwipeGallery} from 'react-photoswipe';

class App extends React.Component {
    state = {
      isOpen: false,
      items: [
      ],
      galleryItems: [
      ],
      options: {}
    };

    openPhotoSwipe = (e) => {
      e.preventDefault();
      this.setState({
        isOpen: true,
        options: {
          closeOnScroll: false
        }
      });
    };

    handleClose = () => {
      this.setState({
        isOpen: false
      });
    };

    getThumbnailContent = (item) => {
      return (
        <img src={item.thumbnail} with={120} height={90}/>
      );
    };

    componentDidMount = () => {
        this.props.data.children.forEach(image => {
            const item = {
                src : image.data.url ,
                thumbnail : image.data.thumbnail,
                w : 1200,
                h : 900
            }
            this.setState({
                galleryItems : [...this.state.galleryItems, item]
            });
        })
    }

    render() {
      var pswpElement = document.querySelectorAll('.pswp')[0];
      var items = [
      {
        src: 'https://placekitten.com/600/400',
        w: 600,
        h: 400
      },
      {
        src: 'https://placekitten.com/1200/900',
        w: 1200,
        h: 900
      }
      ];
      var options = {
        index: 0 
      };
      var gallery = new PhotoSwipe( pswpElement, items, options);
      gallery.init();

        return ( 
            <div className='layout-page'>
            <main className='layout-main'>
                <div className='container'>
                    <h2>PhotoSwipe</h2>
                    <hr/>
                    <button className='btn btn-primary' onClick={this.openPhotoSwipe}>
                    Click me
                    </button>
                    <PhotoSwipe isOpen={this.state.isOpen} items={this.state.items}
                    options={this.state.options}
                    onClose={this.handleClose}/>
                    <hr/>
                    <h2>PhotoSwipeGallery</h2>
                    <hr/>
                    <PhotoSwipeGallery items={this.state.galleryItems}
                    thumbnailContent={this.getThumbnailContent}/>
                </div>
            </main>
            </div>
      );
    }
  }
  export default App;





